/*TV Shows*/
$(function() {
  var b = $("#button1");
  var w = $("#wrapper1");
  var l = $("#list1");

  w.height(l.outerHeight(true));

  b.click(function() {

    if(w.hasClass('open')) {
      w.removeClass('open');
      w.height(0);
    } else {
      w.addClass('open');
      w.height(l.outerHeight(true));
    }

  });
});

